	const format_number_float = (num, allow_negatives=true) => { // ES6
		//--
		if(num == undefined) {
			num = 0;
		} //end if
		num = Number(num);
		num = isFiniteNumber(num) ? num : 0;
		if(allow_negatives !== true) {
			if(num < 0) {
				num = 0;
			} //end if
		} //end if
		num = Number(num);
		//--
		return isFiniteNumber(num) ? num : 0; // Float
		//--
	}; //END
