		str = String((str == undefined) ? '' : str); // force string, test undefined is also for null
		if(str == '') {
			return '';
		} //end if
		//--
		const replacer = (s, n1) => {
			switch(n1) {
				case '\\':
					return '\\';
				case '0':
					return '\u0000';
				case '':
					return '';
				default:
					return String(n1);
			} //end switch
		};
